let L: any = null;
let busStopIcon: any = null;
let busMovingIcon: any = null;
let busStoppedIcon: any = null;
let busOfflineIcon: any = null;

if (typeof window !== 'undefined') {
  L = require('leaflet');
  
  // Configurar iconos por defecto de Leaflet
  delete (L.Icon.Default.prototype as any)._getIconUrl;
  L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
  });

  // Icono para paradas de colectivo
  busStopIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,',
    iconSize: [25, 25],
    iconAnchor: [12, 12],
    popupAnchor: [0, -12],
  });

  // Icono para colectivo en movimiento
  busMovingIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,',
    iconSize: [30, 30],
    iconAnchor: [15, 15],
    popupAnchor: [0, -15],
  });

  // Icono para colectivo parado
  busStoppedIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,',
    iconSize: [28, 28],
    iconAnchor: [14, 14],
    popupAnchor: [0, -14],
  });

  // Icono para colectivo desconectado
  busOfflineIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,',
    iconSize: [26, 26],
    iconAnchor: [13, 13],
    popupAnchor: [0, -13],
  });
}

export { L, busStopIcon, busMovingIcon, busStoppedIcon, busOfflineIcon };